import logo from './logo.svg';
import './App.css';
import { MenuButton, SideMenu } from './components/PageHeader.tsx';
import MeowImage from './components/MeowImage.tsx';
import AnswerInput from './components/AnswerInput';
import AnswerButton from './components/SubmitButton';
import React, {useEffect, useState} from 'react';
import { createTheme } from '@mui/material/styles';
import { Typography } from '@mui/material';

function App() {
  const [open, setOpen] = useState(false);
  const [image, setImage] = useState(null);
  const [name, setName] = useState('');
  const [answer, setAnswer] = useState('');
  const [isCorrect, setIsCorrect] = useState(null);

  const toggleSideMenu = () => {
      return setOpen(!open);
  }

  useEffect(() => {
    const fetchMeow = async () => {
      const data = await fetch('http://localhost:5000/random');
      const json = await data.json();
      const key = Object.keys(json)[0];
      setName(key);
      setImage(json[key]);
    }
      fetchMeow().catch(console.error); 
  }, []);

  const inputChange = (evt) => {
    setAnswer(evt.target.value);
  };

  const validateAnswer = () => {
    console.log(name);
    if(name === answer) {
      setIsCorrect(true);
    } else {
      setIsCorrect(false);
    }
  };

  return (
    <div className="App">
      <header>
        {open && <SideMenu toggle={toggleSideMenu} />}
        {!open && <MenuButton toggle={toggleSideMenu} />}
        <h1>Name That Meow</h1>
      </header>
      <main>
        <h2>Who's This Meow?</h2>
        <MeowImage img={image} />
        <AnswerInput onChange={inputChange} />
        <AnswerButton onClick={validateAnswer} />
        <Result />
      </main>
    </div>
  );
}

export default App;
